import telebot
from telebot import types

bot = telebot.TeleBot(TOKEN)


@bot.message_handler(commands=['start'])
def welcome(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard = True)
    item = types.KeyboardButton("üí™ –í–∏–≤—á–µ–Ω–Ω—è –≤–ø—Ä–∞–≤")
    markup.add(item)
    bot.send_message(message.chat.id, "–ü—Ä–∏–≤—ñ—Ç, {0.first_name}!\n–Ø ‚Äî <b>{1.first_name}</b>, –±–æ—Ç —Å—Ç–≤–æ—Ä–µ–Ω–∏–π, —â–æ–± –Ω–∞–≤—á–∏—Ç–∏ —Ä–æ–±–∏—Ç–∏ –≤–ø—Ä–∞–≤–∏ –ø—Ä–∞–≤–∏–ª—å–Ω–æ.".format(message.from_user, bot.get_me()), parse_mode = 'html', reply_markup = markup)


